import pdfMake from 'pdfmake/build/pdfmake';
import pdfFonts from 'pdfmake/build/vfs_fonts';
import { TDocumentDefinitions } from 'pdfmake/interfaces';
import { format } from "date-fns";
import { deleteCookie, getCookie } from 'cookies-next';

export async function generatePDF(rateInfo: RatesInfo, futureRateRequested: boolean) {
    const RATRFSNumber = getCookie('RATRFSNumber');
    const WFRRFSNumber = getCookie('WFRRFSNumber');

    const dateOptions = {
        year: 'numeric',
        month: 'long',
        day: 'numeric'
    }

    function reformatAddress(inputString: string): string {
        let stringWithHyphens = inputString.replace(/\s+/g, '-');
        stringWithHyphens = stringWithHyphens.toLowerCase();
        const currentDate = new Date();
        const dateSuffix = `-${currentDate.getFullYear()}-${(currentDate.getMonth() + 1).toString().padStart(2, '0')}-${currentDate.getDate().toString().padStart(2, '0')}`;
        return stringWithHyphens + dateSuffix;
    }

    //const vfs = pdfFonts.pdfMake.vfs;

    const fonts = {
        Din2014: {
            normal: "https://dccwebteam-app.github.io/solicitors-portal/portal/assets/fonts/din-2014/din-2014-regular.ttf",
            bold: 'https://dccwebteam-app.github.io/solicitors-portal/portal/assets/fonts/din-2014/din-2014-bold.ttf',
        },
        Roboto: {
            normal: 'https://cdnjs.cloudflare.com/ajax/libs/pdfmake/0.1.66/fonts/Roboto/Roboto-Regular.ttf',
            bold: 'https://cdnjs.cloudflare.com/ajax/libs/pdfmake/0.1.66/fonts/Roboto/Roboto-Medium.ttf',
            italics: 'https://cdnjs.cloudflare.com/ajax/libs/pdfmake/0.1.66/fonts/Roboto/Roboto-Italic.ttf',
            bolditalics: 'https://cdnjs.cloudflare.com/ajax/libs/pdfmake/0.1.66/fonts/Roboto/Roboto-MediumItalic.ttf'
        },
    };
    // define custom table layouts 
    const tableLayouts = {
        generalLayout: {
            hLineWidth: function () {
                return 0;
            },
            vLineWidth: function () {
                return 0;
            },
            hLineColor: function (i: any) {
                return i === 1 ? 'black' : '#aaa';
            },
            paddingLeft: function (i: any) {
                return i === 0 ? 0 : 8;
            },
            paddingTop: function () {
                return 8;
            },
            paddingBottom: function () {
                return 8;
            },
            paddingRight: function (i: any, node: any) {
                return (i === node.table.widths.length - 1) ? 0 : 8;
            }
        }
    };
    let ratepayerName: string = '';
    rateInfo?.ratePayers.map(nameObject => {
        ratepayerName = nameObject.name + "\n\n" + ratepayerName
    })
    // let ratepayerName = rateInfo?.ratePayers.reduce((acc, currentValue) => acc + "\n\n" + currentValue);
    let rateInstalments = rateInfo?.instalments.sort((a, b) => {
        return new Date(a.date).getTime() - new Date(b.date).getTime()
    }).map((installment) => { return installment })
    const docDefinition: TDocumentDefinitions = {
        info: {
            title: 'Rating information as at 26/01/2024', // dynamic value
            author: 'Dunedin City Council',
            subject: 'Rates Search Information',
            keywords: 'DCC, Solicitors Portal, Rates Search',
            creator: 'Dunedin City Council',
            producer: '-'
        },
        permissions: {
            printing: 'highResolution',
            modifying: false,
            contentAccessibility: false,
            copying: false
        },
        content: [
            // define logo
            {
                image: "data:image/jpeg;base64,",
                width: 160
            },
            // define new line
            { text: '\n\n' },
            {
                text: 'The information detailed on this page is provided based on the details you have entered in the Portal. The DCC has made all reasonable endeavours to ensure the accuracy of information.'
            },
            // define new line
            { text: '\n' },
            // define table layout 
            {
                layout: 'generalLayout', // custom table layout
                table: {
                    body: [
                        [{ text: 'Rating Details as at', bold: true }, { text: ':', bold: true }, { text: format(rateInfo?.ratingDetailsDate, "dd/MM/yyy"), margin: [0, 2, 0, 0] }],
                        [{ text: 'Our Rates Reference', bold: true }, { text: ':', bold: true }, { text: rateInfo?.rateReferenceId, margin: [0, 2, 0, 0] }],
                        [{ text: 'Property Address', bold: true }, { text: ':', bold: true }, { text: rateInfo?.propertyInfo.address, margin: [0, 2, 0, 0] }],
                        [{ text: 'Valuation Number', bold: true }, { text: ':', bold: true }, { text: rateInfo?.propertyInfo.valuationNumber, margin: [0, 2, 0, 0] }],
                        [{ text: 'Legal Description', bold: true }, { text: ':', bold: true }, { text: rateInfo?.propertyInfo.legalDescription, margin: [0, 2, 0, 0] }],
                        [{ text: 'Rate Payers', bold: true }, { text: ':', bold: true }, {
                            text: [


                                { text: ratepayerName },
                                // { text: 'Paula Marie Cotter' }
                            ],

                            margin: [0, 2, 0, 0]
                        }],
                        [{ text: 'Annual Rates', bold: true }, { text: ':', bold: true }, { text: "$" + rateInfo?.annualRateAmount.toFixed(2), margin: [0, 2, 0, 0] }],
                    ],
                    headerRows: 0,
                    widths: [120, 10, 'auto'],
                }
            },
            // define new line
            { text: '\n' },
            // instalement table 
            {
                layout: 'lightHorizontalLines', // table alyout

                table: {
                    body: rateInfo && rateInfo.instalments ? [
                        [{ text: 'Instalment', bold: true, margin: [0, 0, 0, 0] },
                        { text: 'Date', bold: true, margin: [0, 0, 0, 0] },
                        { text: 'Amount', alignment: 'right', bold: true, margin: [0, 0, 0, 0] },
                        { text: 'Penalty', alignment: 'right', bold: true, margin: [0, 0, 0, 0] },
                        { text: 'Total', alignment: 'right', bold: true, margin: [0, 0, 0, 0] },
                        { text: 'Balance', alignment: 'right', bold: true, margin: [0, 0, 0, 0] },
                        { text: 'Due Date', bold: true, margin: [0, 0, 0, 0] }]].concat(
                            rateInfo.instalments.map((instalment, index) => {
                                return [
                                    { text: 'Instalment ' + (index + 1), bold: false, margin: [0, 6, 0, 6] },
                                    { text: instalment ? format(instalment.date, "dd/MM/yyy") : '', bold: false, margin: [0, 6, 0, 6] },
                                    { text: instalment ? "$" + instalment.amount.toFixed(2) : '', alignment: 'right', bold: false, margin: [0, 6, 0, 6] },
                                    { text: instalment && instalment.penalty && !isNaN(Number(instalment.penalty)) ? "$" + instalment.penalty.toFixed(2) : '-', alignment: 'right', bold: false, margin: [0, 6, 0, 6] },
                                    { text: instalment ? "$" + (instalment.total).toFixed(2) : '$0.00', alignment: 'right', bold: false, margin: [0, 6, 0, 6] },
                                    { text: instalment ? "$" + instalment.balance.toFixed(2) : '', alignment: 'right', bold: false, margin: [0, 6, 0, 6] },
                                    { text: instalment && instalment.dueDate ? format(instalment.dueDate, "dd/MM/yyy") : '', bold: false, margin: [0, 6, 0, 6] }
                                ]
                            }))
                        : [],
                    headerRows: 1,
                    widths: [65, 60, 60, 60, 60, 60, 'auto'],
                }
            },
            // define new line
            { text: '\n' },
            // define text
            { text: 'A 10% penalty will be added to any unpaid portion of the current instalment if not paid by the Tuesday following the due date.' },
            // define new line
            { text: '\n' },
            { text: [{ text: 'Exceptions', bold: true }, { text: ' - a penalty will not be added if there is a direct debit in place or one has been recently cancelled as part of the settlement process.' }] },
            // define new line
            { text: '\n' },
            rateInfo && rateInfo.penaltyWaiver ? [
                {
                    layout: 'generalLayout',
                    table: {
                        body: rateInfo && rateInfo.penaltyWaiver ?
                            rateInfo.penaltyWaiver.map((penalty, index) => {
                                return [
                                    { text: 'Penalty Waiver [' + format(penalty.date, "dd/MM/yyy") + ']', bold: true }, { text: ':', bold: true }, { text: "$" + penalty.amount.toFixed(2), alignment: 'right', margin: [0, 2, 0, 0] }
                                ]
                            })
                            : [],
                        headerRows: 0,
                        widths: [210, 10, 'auto'],
                    }
                }
            ] : [],
            {
                layout: 'generalLayout',
                table: {
                    body: [
                        [{ text: 'Current Balance', bold: true }, { text: ':', bold: true }, { text: "$" + rateInfo?.currentBalance.toFixed(2), alignment: 'right', margin: [0, 2, 0, 0] }],
                        [{ text: 'Total to clear rates to end of rating year', bold: true }, { text: ':', bold: true }, { text: "$" + rateInfo?.totalClearBalance.toFixed(2), alignment: 'right', margin: [0, 2, 0, 0] }],
                    ],
                    headerRows: 0,
                    widths: [210, 10, 'auto'],
                }
            },

            // define new line - future rates
            futureRateRequested && rateInfo.futureRates.showRID && rateInfo.futureRates.rates ? [{ text: '\n' },
            { text: 'Future Rates', bold: true },
            {
                layout: 'generalLayout',
                table: {
                    body: rateInfo.futureRates && rateInfo.futureRates.rates ?
                        rateInfo.futureRates.rates.map((rate) => {
                            return [
                                { text: rate.description, bold: false }, { text: ':', bold: false }, { text: rate.amount ? "$" + rate.amount.toFixed(2) : "$0.00", margin: [0, 2, 0, 0] }
                            ]
                        })
                        : [],
                    headerRows: 0,
                    widths: [210, 10, 'auto'],
                }
            },
            {
                layout: 'generalLayout',
                table: {
                    body: rateInfo.futureRates && rateInfo.futureRates.rates ?
                        [
                            [{ text: "Total", bold: false }, { text: ':', bold: false }, { text: rateInfo.futureRates.total ? "$" + rateInfo.futureRates.total.toFixed(2) : "$0.00", margin: [0, 2, 0, 0] }]
                        ]
                        : [],
                    headerRows: 0,
                    widths: [210, 10, 'auto'],
                }
            }] : [{ text: ' ' }],

            // define new line - direct debits
            rateInfo && rateInfo.directDebits ? [{ text: '\n' },
            { text: 'Direct Debit', bold: true },
            {
                layout: 'generalLayout',
                table: {
                    body: rateInfo && rateInfo.directDebits ? [
                        [{ text: 'Frequency', bold: true }, { text: ':', bold: true }, { text: rateInfo.directDebits.frequency ? rateInfo?.directDebits.frequency : '-', margin: [0, 2, 0, 0] }],
                        [{ text: 'Amount', bold: true }, { text: ':', bold: true }, { text: (rateInfo.directDebits.amount && Number(rateInfo.directDebits.amount) != 0) ? (isNaN(Number(rateInfo.directDebits.amount)) ? rateInfo.directDebits.amount : "$" + Number(rateInfo.directDebits.amount).toFixed(2)) : "$" + "0.00", alignment: 'left', margin: [0, 2, 0, 0] }],
                        [{ text: 'Request to cancel', bold: true }, { text: ':', bold: true }, { text: (RATRFSNumber && Number(RATRFSNumber) != 0) ? ("In progress - RFS" + RATRFSNumber + "") : "-", margin: [0, 2, 0, 0] }],
                    ] : [],
                    headerRows: 0,
                    widths: [210, 10, 'auto'],
                }
            }] : [{ text: ' ' }],

            // define new line
            !rateInfo.directDebits ? [
                [{ text: 'There is no direct debit on this account. If the rates are paid by automatic payment, please ask the customer to cancel it.', bold: true }]
            ] : [{ text: '  ' }],

            {
                layout: 'generalLayout',
                table: {
                    body: [
                        [{ text: 'Last Payment Received', bold: true }, { text: ':', bold: true }, { text: rateInfo.lastPaymentReceived ? format(rateInfo.lastPaymentReceived, "dd/MM/yyy") : '', margin: [0, 2, 0, 0] }],
                    ],
                    headerRows: 0,
                    widths: [120, 10, 'auto'],
                }
            },

            // define new line - direct debits
            rateInfo.waterRates ? [
                {
                    layout: 'generalLayout',
                    table: {
                        body: [
                            [{ text: 'Final Water Reading', bold: true }, { text: ':', bold: true }, { text: (WFRRFSNumber && Number(WFRRFSNumber) != 0) ? ((rateInfo.finalWaterReadingDate ? rateInfo.finalWaterReadingDate.toLocaleString().slice(0, 10) : "") + " - RFS" + WFRRFSNumber + "") : "-", margin: [0, 2, 0, 0] }]
                        ],
                        headerRows: 0,
                        widths: [120, 10, 'auto'],
                    }
                },] : [{ text: ' ' }],


            //payment details
            { text: '\n' },
            { text: '\n' },
            { text: 'Details for payment', bold: true },
            {
                layout: 'generalLayout',
                table: {
                    body: [
                        [{ text: 'Dunedin City Council', bold: false }, { text: '03 0905 0184000 10', margin: [0, 0, 0, 0] }],
                        [{ text: 'Particulars ', bold: false }, { text: rateInfo?.rateReferenceId, margin: [0, 0, 0, 0] }],
                        [{ text: 'Code', bold: false }, { text: 'Rates', margin: [0, 0, 0, 0] }],
                        [{ text: 'Reference', bold: false }, { text: rateInfo?.rateReferenceId, margin: [0, 0, 0, 0] }],
                    ],
                    headerRows: 0,
                    widths: [120, 'auto'],
                }
            },
            { text: '\n' },
            { text: 'Unfortunately we are unable to provide the exact amount to clear the rates to a particular date; you will need to apportion this if required.', bold: false },
            { text: '\n' },
            { text: [{ text: 'For Otago Regional Council rates, please contact ', bold: false }, { text: 'info@orc.govt.nz', link: 'mailto:info@orc.govt.nz', color: 'blue' }] }
        ],
        // define page margins
        pageMargins: [40, 30, 40, 50],
        // define footer
        footer: function (currentPage, pageCount) {
            return {
                text: [{ text: '50 The Octagon', fontSize: 9 }, { text: '  |  ', fontSize: 9 }, { text: 'PO Box 5045', fontSize: 9 }, { text: '  |  ', fontSize: 9 }, { text: 'Dunedin 9054, New Zealand', fontSize: 9 }, { text: '  |  ', fontSize: 9 }, { text: 'T ', fontSize: 9, bold: true }, { text: ' 03 477 4000', link: "tel:+643277400", fontSize: 9 }, { text: '  |  ', fontSize: 9 }, { text: 'E ', fontSize: 9, bold: true }, { text: 'dcc@dcc.govt.nz', link: "mailto:dcc@dcc.govt.nz", fontSize: 9 }, { text: '  |  ', fontSize: 9 }, { text: 'www.dunedin.govt.nz', link: "https://www.dunedin.govt.nz", fontSize: 9 }, { text: "\n\n" }, { text: 'Facebook: ', fontSize: 9, bold: true }, { text: 'DunedinCityCouncil', link: "https://www.facebook.com/DunedinCityCouncil", fontSize: 9 }],
                alignment: 'left',
                margin: [40, 0, 40, 0]
            };
        },
        defaultStyle: {
            font: 'Din2014'
        },
    };
    pdfMake.createPdf(docDefinition, tableLayouts, fonts).download(reformatAddress(rateInfo?.propertyInfo.address) + '.pdf');
}
